// Функция для парсинга количества слотов из строки options
function parseSlotsFromOptions(optionsStr) {
    if (!optionsStr) return 1;
    
    // Особые случаи
    if (optionsStr.includes('не требует слотов')) return 0;
    
    // Проверяем специфичные паттерны сначала
    if (optionsStr.match(/(\d+)\s*по\s*1\s*слоту?/)) {
        const singleMatches = optionsStr.match(/(\d+)\s*по\s*1\s*слоту?/);
        if (singleMatches) return parseInt(singleMatches[1]);
    }

    if (optionsStr.includes('по 1')) {
        const multipleMatches = optionsStr.match(/по 1.*?в.*?(\d+)/);
        if (multipleMatches) return parseInt(multipleMatches[1]);
        return 1;
    }
    if (optionsStr.includes('суммарно')) {
        const totalMatches = optionsStr.match(/суммарно\s*(\d+)/i);
        if (totalMatches) return parseInt(totalMatches[1]);
    }
    if (optionsStr.includes('два места')) {
        const placesMatches = optionsStr.match(/два места по (\d+)/i);
        if (placesMatches) return parseInt(placesMatches[1]) * 2;
    }
    if (optionsStr.match(/(\d+)\s*по\s*(\d+)/)) {
        const multiMatches = optionsStr.match(/(\d+)\s*по\s*(\d+)/);
        if (multiMatches) return parseInt(multiMatches[1]) * parseInt(multiMatches[2]);
    }

    // Ищем числа в строке (только если не найдены специфичные паттерны)
    const matches = optionsStr.match(/(\d+)\s*слот/i);
    if (matches) {
        return parseInt(matches[1]);
    }
    
    return 1; // По умолчанию 1 слот
}

// Функция для определения типа установки модуля
function getModuleInstallType(optionsStr) {
    if (!optionsStr) return 'simple';
    
    // Специальные типы установки
    if (optionsStr.includes('два места по')) {
        return 'two_locations';
    }
    if (optionsStr.includes('суммарно') && optionsStr.includes('в разных частях')) {
        return 'distributed_spine';
    }
    if (optionsStr.match(/(\d+)\s*по\s*1\s*слоту?/)) {
        return 'multiple_single_slots';
    }
    
    return 'simple'; // Обычная установка подряд
}

// Функция для нормализации модулей имплантов - добавляет недостающие поля
function normalizeImplantModules() {
    // Специальные настройки для известных модулей
    const moduleSpecialData = {
        "Баллистический щит": { durability: 20, maxDurability: 20 },
        "Встроенное оружие": { weaponType: "ПП", magazine: 30, currentAmmo: 30 },
        "Интегрированный гранатомёт": { weaponType: "Гранатомёт", magazine: 1, currentAmmo: 1 },
        // Сложные модули с особыми правилами установки
        "Крылья для планирования": { specialInstall: "spine_all_parts", totalSlots: 4 },
        "Прыжковый ускоритель": { specialInstall: "two_locations_2slots", totalSlots: 4 },
        "Гребные винты": { specialInstall: "flexible_2slots", totalSlots: 2 },
        "Реактивные стабилизаторы": { specialInstall: "four_parts_1slot", totalSlots: 4 },
        "Эндоскелет": { specialInstall: "arms_legs_spine_each", totalSlots: 11 }, // 6 частей рук + 6 ног + 5 спины = 17, но по 1 в каждой
        "Оптический камуфляж": { specialInstall: "external_parts_2slots", totalSlots: 8 }, // голова, спина, руки, ноги - в одной части каждого по 2
        // Укрепления костной системы
        "Внутреннее укрепление полимером": { specialInstall: "bone_reinforcement", os: 15, type: "polymer" },
        "Внутреннее укрепление полиметаллом": { specialInstall: "bone_reinforcement", os: 18, type: "polymetal" }
    };
    
    for (const category in CYBERIMPLANTS_LIBRARY) {
        CYBERIMPLANTS_LIBRARY[category].forEach(module => {
            // Добавляем slots если отсутствует
            if (!module.slots) {
                module.slots = parseSlotsFromOptions(module.options);
            }
            
            // Добавляем специальные данные если есть
            if (moduleSpecialData[module.name]) {
                Object.assign(module, moduleSpecialData[module.name]);
            }
            
            // Парсим requiredParts из options
            if (module.options && module.options.includes('разных частях')) {
                const partsMatch = module.options.match(/(\d+)\s*разных частях/);
                if (partsMatch) {
                    module.requiredParts = parseInt(partsMatch[1]);
                }
            }
        });
    }
}

